teamMaturity.map:
function (doc) {
  if (doc.type == 'matassessmtrslt'  && (doc.doc_status === null || doc.doc_status != 'delete'))
    emit(doc.team_id, doc);
}

teamSearch.index:
function (doc) {
  if (doc.type == 'matassessmtrslt' && (doc.doc_status === null || doc.doc_status != 'delete')) {
    index("default", doc.team_id);
    var date = "";
    if (doc.team_id) {
      index("team_id", doc.team_id, {"store": true, "facet": true});
    }
    if (doc.created_dt) {
      index("created_dt", doc.created_dt, {"store": true, "facet": true});
    }
    if (doc["self-assessmt_dt"]) {
      index("self-assessmt_dt", doc["self-assessmt_dt"], {"store": true, "facet": true});
    }
    if (doc.assessmt_status) {
      index("assessmt_status", doc.assessmt_status, {"store": true, "facet": true});
			if (doc["assessmt_status"].toLowerCase() == "draft") {
        date = doc.created_dt.split("-");
        if (date.length == 1) {
          date = doc.created_dt.split("/");
          date = parseInt(date[2]+date[0]+date[1]);
        } else
          date = parseInt(date[0]+date[1]+date[2]);
        index("sort_dt", date, {"store": false, "facet": true});			  
      } else if (doc["assessmt_status"].toLowerCase() == "submitted") {
        date = doc["self-assessmt_dt"].split("-");
        if (date.length == 1) {
          date = doc["self-assessmt_dt"].split("/");
          date = parseInt(date[2]+date[0]+date[1]);
        } else
          date = parseInt(date[0]+date[1]+date[2]);
        index("sort_dt", date, {"store": false, "facet": true});
      } else {
        date = doc.created_dt.split("-");
        if (date.length == 1) {
          date = doc.created_dt.split("/");
          date = parseInt(date[2]+date[0]+date[1]);
        } else
          date = parseInt(date[0]+date[1]+date[2]);
        index("sort_dt", date, {"store": false, "facet": true});		
      }
    }
  }
}
