completed.map:
function (doc) {
  if (doc.type == "iterationinfo" && (doc.doc_status === null || doc.doc_status != 'delete')) {
    if (doc.iterationinfo_status == "Completed") {
          emit(doc.iteration_end_dt, doc);
    }
  }
}

notCompleted.map:
function (doc) {
  if (doc.type == "iterationinfo" && (doc.doc_status === null || doc.doc_status != 'delete')) {
    if (doc.iterationinfo_status != "Completed") {
          emit(doc.iteration_end_dt, doc);
    }
  }
}

teamIteration.map:
function (doc) {
  if (doc.type == "iterationinfo" && (doc.doc_status === null || doc.doc_status != 'delete')) {
    emit(doc.team_id, 
        {
            'team_id': doc.team_id,
            'iteration_name': doc.iteration_name,
            'iteration_start_date' : doc.iteration_start_date,
            'iteration_end_date' : doc.iteration_start_date,
            'iteration_status' : doc.iteration_status
        });
  }
}

