searchAll.index:
function (doc) {
  if (doc.type == "iterationinfo" && (doc.doc_status === null || doc.doc_status != 'delete')) {
    index("default", doc.team_id); 
    if (doc.iterationinfo_status) {
      var completed = doc.iterationinfo_status.toUpperCase() == "COMPLETED" ? "Y" : "N";
      index("completed", completed, {"store": true, "facet": true});
      index("iterationinfo_status", doc.iterationinfo_status, {"store": true, "facet": true});
    }
    if (doc.iteration_end_dt) {
      var date = doc.iteration_end_dt.split("/");
      index("end_dt", parseInt(date[2]+date[0]+date[1]), {"store": true, "facet": true});
      index("iteration_end_dt", doc.iteration_end_dt, {"store": true, "facet": true});
    }
    if (doc.iteration_start_dt) {
      var date2 = doc.iteration_start_dt.split("/");
      index("start_dt", parseInt(date2[2]+date2[0]+date2[1]), {"store": true, "facet": true});
      index("iteration_start_dt", doc.iteration_start_dt, {"store": true, "facet": true});
    }
       if (doc.iteration_name) {
      index("name", doc.iteration_name, {"store": true, "facet": true});
    }
    if (doc.team_id) {
      index("team_id", doc.team_id, {"store": true, "facet": true});
    }
  if (doc._id) {
      index("id", doc._id, {"store": true, "facet": true});
    }
 }
}
