teams.map:
function (doc) {
  if ((doc.type == 'team') && (doc.doc_status === null || doc.doc_status != 'delete')) {
    var teamMembers = [];
    var teamCount = 0;
    var teamAlloc = 0;
    for (var i in doc.members) {
  		if (teamMembers.indexOf(doc.members[i].id) == -1) {
  			teamCount++;
  			teamMembers.push(doc.members[i].id);
  		}
  		teamAlloc += parseInt(doc.members[i].allocation);
  	}
  	teamAlloc = teamAlloc/100;
  	emit(doc._id, 
      {
        '_id': doc._id, 
        'name': doc.name, 
        'squadteam': doc.squadteam,
        'parent_team_id': doc.parent_team_id,
        'child_team_id': doc.child_team_id,
        'total_members': teamCount,
        'total_allocation': teamAlloc
      });
  }
}

teamsWithMember.map:
function (doc) {
  if (doc.type == "team" && (doc.doc_status === null || doc.doc_status != 'delete')) {
    if (doc.members !== undefined && doc.members.length > 0) {
      for(i=0; i<doc.members.length; i++)
          emit(doc.members[i].id, 
          { 
            '_id': doc._id,
            '_rev': doc._rev,
            'name': doc.name,
            'parent_team_id': doc.parent_team_id,
            'child_team_id': doc.child_team_id,
            'squadteam': doc.squadteam
          }); 
    }
  }
}

teamNames.map:
function (doc) {
  if ((doc.type == 'team') && (doc.doc_status === null || doc.doc_status != 'delete'))
      emit(doc.name, {_id: doc._id, name: doc.name}); 
}

lookup.map:
function (doc) {
  if (doc._id == "ag_ref_team_index") {
    for (var i in doc.domains)
      emit(doc.domains[i]._id, doc.domains[i]);
    for (i in doc.tribes)
      emit(doc.tribes[i]._id, doc.tribes[i]);
    for (i in doc.squads)
      emit(doc.squads[i]._id, doc.squads[i]);
  }
}

lookupTeamsWithSquad.map:
function (doc) {
  if (doc._id == "ag_ref_team_index") {
    for (var i in doc.squads) {
      for (var j in doc.squads[i].parents)
        emit(doc.squads[i].parents[j], 
          {
            _id: doc.squads[i]._id,
            name: doc.squads[i].name
          }
          );
    }
  }
}

name.index:
function (doc) {
  if (doc.type == "team" && (doc.doc_status === null || doc.doc_status != 'delete')) {
    index("default", doc.name); 
    if (doc.name) {
      index("name", doc.name, {"store": true, "facet": true});
    }
    if (doc.squadteam) {
      index("squad", doc.squadteam, {"store": true, "facet": true});
    }
  }
}
